mapper.insert:

	SELECT ifnull(max(board_no)+1,1) FROM board

	INSERT INTO board(
 MEMBER_NO
,BOARD_TITLE
,BOARD_DATE
,BOARD_CONTENT
,BOARD_HIT_COUNT
,BOARD_LIKE_COUNT
,BOARD_REPORT_COUNT
,BOARD_GROUP_NO
,BOARD_TAG_NAME
)
VALUES(
#{member_no}
,#{board_title}
,NOW()
,#{board_content}
,0
,0
,0
,#{board_group_no}
,#{board_tag_name}
)

mapper.update:
UPDATE board SET
BOARD_FILE_CHECK = 'Y'
WHERE BOARD_NO = #{board_no}

UPDATE bfile SET
DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}

mapper.insert:

	SELECT ifnull(max(bcomment_no)+1,1) FROM bcomment


INSERT INTO bcomment
VALUES(
 #{bcomment_no}
,#{board_no}
,#{bcomment_content}
,NOW()
,#{member_no}
,0
,0
,#{ref_step}
,0
)

mapper.update:
UPDATE board
SET
 BOARD_TITLE = #{board_title}
 ,BOARD_CONTENT = #{board_content}
 ,BOARD_GROUP_NO = #{board_group_no}
 ,BOARD_TAG_NAME = #{board_tag_name}
WHERE BOARD_NO = #{board_no} AND MEMBER_NO = #{member_no}

mapper.select:
SELECT IFNULL(MAX(REF_LEVEL),0) FROM bcomment
WHERE BOARD_NO  = #{board_no}
AND REF_STEP = #{ref_step}

mapper.insert:

SELECT IFNULL(max(bcomment_no)+1,1) FROM bcomment

