mapper.update:
UPDATE  board SET 
BOARD_TAG_NAME = ''
WHERE BOARD_NO = #{board_no}

mapper.delete:
		DELETE FROM bcomment
        WHERE 
            BOARD_NO=#{board_no} 
			AND
            REF_STEP=#{ref_step}

mapper.update:
UPDATE bcomment
SET
BCOMMENT_DATE = NOW(),
BCOMMENT_CONTENT = #{bcomment_content}
WHERE 
BCOMMENT_NO = #{bcomment_no}

UPDATE bcomment
SET
BCOMMENT_CONTENT = #{bcomment_content},
BCOMMENT_DATE = NOW()
WHERE
BOARD_NO = #{board_no}
AND
REF_STEP = #{ref_step}
AND
REF_LEVEL = #{ref_level}
